import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
 const [amount, setAmount] = useState(0);
 const [fromCurrency, setFromCurrency] = useState('USD');
 const [currentUSD, setCurrentUSD] = useState('USD');
 const [toCurrency, setToCurrency] = useState('EUR');
 const [convertedAmount, setConvertedAmount] = useState(0);
 const [exchangeRate, setExchangeRate] = useState(0); // State to store and display the exchange rate
 const [fromCurrencyRate, setFromCurrencyRate] = useState(0); // New state to store the exchange rate for the fromCurrency

 const currencies = [
   "USD", "EUR", "GBP", "JPY", "BLUR", "bNEO", "BUSD", "ETH", "GMX", "STEVMOS",
   "LUNA", "RATOM", "STRD", "EVMOS", "IBCX", "IRIS", "ampLUNA", "KUJI", "STOSMO",
   "USDC", "ATOM", "STATOM", "OSMO", "rSWTH", "STLUNA", "LSI", "OKB", "OKT",
   "SWTH", "USC", "WBTC", "wstETH", "YieldUSD", "ZIL"
 ];

 const handleCurrencyChange = async (event, type) => {
    if (type === 'from') {
      setFromCurrency(event.target.value);
    } else {
      setToCurrency(event.target.value);
    }
    // Fetch current rates and update exchange rate immediately
    const rates = await fetchCurrentRates();
    const rate = rates[fromCurrency] / rates[toCurrency];
    setExchangeRate(rate);
    // Update the exchange rate for the fromCurrency
    setCurrentUSD(rates[fromCurrency]);
 };

 const handleAmountChange = (event) => {
    setAmount(event.target.value);
 };

 const fetchCurrentRates = async () => {
    // Use the provided JSON data for fetching the current exchange rates
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          "BLUR": 0.20811525423728813,
          "bNEO": 7.1282679,
          "BUSD": 0.999183113,
          "ETH": 1645.9337373737374,
          "GMX": 36.345114372881355,
          "STEVMOS": 0.07276706779661017,
          "LUNA": 0.40955638983050846,
          "RATOM": 10.250918915254237,
          "STRD": 0.7386553389830508,
          "EVMOS": 0.06246181355932203,
          "IBCX": 41.26811355932203,
          "IRIS": 0.0177095593220339,
          "ampLUNA": 0.49548589830508477,
          "KUJI": 0.675,
          "STOSMO": 0.431318,
          "USDC": 0.989832,
          "ATOM": 7.186657333333334,
          "STATOM": 8.512162050847458,
          "OSMO": 0.3772974333333333,
          "rSWTH": 0.00408771,
          "STLUNA": 0.44232210169491526,
          "LSI": 67.69661525423729,
          "OKB": 42.97562059322034,
          "OKT": 13.561577966101694,
          "SWTH": 0.004039850455012084,
          "USC": 0.994,
          "WBTC": 26002.82202020202,
          "wstETH": 1872.2579742372882,
          "YieldUSD": 1.0290847966101695,
          "ZIL": 0.01651813559322034,
          "USD": 1,
          "EUR": 0.85,
          "GBP": 1.37,
          "JPY": 110.31,
        });
      }, 1000);
    });
 };

 const handleConversion = async () => {
    const rates = await fetchCurrentRates();
    const rate = rates[fromCurrency] / rates[toCurrency];
    setConvertedAmount(amount * rate);
 };

 return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>Currency Converter</p>
        <div>
          <label>Amount:</label>
          <input type="number" value={amount} onChange={handleAmountChange} />
        </div>
        <div>
          <label>From:</label>
          <select value={fromCurrency} onChange={(e) => handleCurrencyChange(e, 'from')}>
            {currencies.map((currency) => (
              <option key={currency} value={currency}>{currency}</option>
            ))}
          </select>
          {currentUSD > 0 && (
            <span>1 {fromCurrency} = {setCurrentUSD.toFixed(5)} USD</span>
          )}
        </div>
        <div>
          <label>To:</label>
          <select value={toCurrency} onChange={(e) => handleCurrencyChange(e, 'to')}>
            {currencies.map((currency) => (
              <option key={currency} value={currency}>{currency}</option>
            ))}
          </select>
        </div>
        <button onClick={handleConversion}>Convert</button>
        {convertedAmount > 0 && (
          <div>
            <p>Converted Amount: {convertedAmount.toFixed(5)} {toCurrency}</p>
            <p>Exchange Rate: 1 {fromCurrency} = {exchangeRate.toFixed(5)} {toCurrency}</p>
          </div>
        )}
      </header>
    </div>
 );
}

export default App;
