import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
 const [isOpen, setIsOpen] = useState(false);

 const toggleConversionBox = () => {
  setIsOpen(!isOpen);
  const conversionBox = document.querySelector('.conversion-box');
  if (conversionBox) {
     conversionBox.classList.toggle('open', isOpen);
  }
 };

 return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          test
        </p>
        <button onClick={toggleConversionBox}>Toggle Conversion Box</button>
        {isOpen && (
          <div className="conversion-box">
            <h2>Conversion Box</h2>
            <p>This is a simple conversion box.</p>
          </div>
        )}
      </header>
    </div>
 );
}




export default App;
